triggers.on_activate_expr:
radio("Company this is Alpha. I Read you five by five.");
set_objective("lane_1_comms", 1);
sleep_ui(2.0);
tutorial_dialog("Excellent! You just performed a radio check. You'll use the radio to give all commands to your units.", "%TranscriptDoc", true);
sleep_ui(1.0);
tutorial_dialog("The Radio Transcript shows all messages. Use it to review what was said during the mission.", "%TranscriptDoc", true);
sleep_ui(1.0);
tutorial_dialog("This drawer contains blank unit counters. You can create markers to track friendly and enemy units.", "%UnitCounterDrawer", true);
tutorial_dialog("Click the drawer, then create a counter for UMPIRE: Affiliation=Neutral, Type=HQ, Size=Team, Callsign=UMPIRE.", "%UnitCounterDrawer", true);

set_global("is_tool_tut_finished", false);

sleep_ui(2.0);
tutorial_dialog("Welcome to Operational Command! This tutorial mission will teach you the basics of commanding your units.", "%Map", true);
sleep_ui(1.0);
tutorial_dialog("This is your HQ table. You command units by talking on the radio, tracking them on the map, and reading documents.", "%Map", true);
sleep_ui(1.0);
tutorial_dialog("This briefing document shows your mission objectives. Drag it with LMB, double-click to open, RMB to close.", "%BriefingDoc", true);
sleep_ui(1.0);
tutorial_dialog("This is your radio - the most important tool. Press and HOLD SPACE to transmit, then speak your command.", "%Radio", true);
tutorial_dialog("Let's try it! Hold SPACE and say: 'Radio Check' (then release SPACE).", "%Radio", true);
set_global("is_tool_tut_finished", true);

set_objective("draw_plan", 1)

sleep_ui(1.0);
tutorial_dialog("Great! You've drawn your maneuver plan. Now let's order ALPHA to execute it.", "%Map", true);
sleep_ui(1.0);
tutorial_dialog("Hold SPACE and say: 'ALPHA, move to OBJ 1' or 'ALPHA, move to grid 633858' (OBJ 1's grid coordinate).", "%Radio", true);

set_objective("set_time", 1)

sleep_ui(1.0);
tutorial_dialog("Good! Time is running. ALPHA is now executing your movement order. You can pause/play anytime.", "%Timer", true);
sleep_ui(1.0);
tutorial_dialog("The other documents on the table (Intel, Protractor, Field Manual) provide reference info - check them out later!", "%IntelDoc", true);
sleep(15.0);
tutorial_dialog("You can request reports from units. Hold SPACE and try: 'ALPHA, report position' or 'ALPHA, report status'", "%Radio", true);

set_objective("unit_markers", 1)

sleep_ui(2.0);
tutorial_dialog("Perfect! Unit counters help you track units on the map. You can pcik them up andplace them anywhere using LMB.", "%Map", true);
sleep_ui(1.0);
tutorial_dialog("These are drawing tools. Click a pen to pick it up, then hold LMB on the map to draw. Right-click to put it down.", "%PenBlue", true);
tutorial_dialog("Let's plan a route to OBJ 1. Pick the BLUE pen and draw arrows from WP 1 → WP 2 → WP 3 → OBJ 1.", "%Map", true);

sleep_ui(1.0);
tutorial_dialog("Perfect! ALPHA has received your order. Orders are queued until you start time.", "%Map", true);
sleep_ui(1.0);
tutorial_dialog("This is the mission timer. Click the PLAY button to start the simulation and watch ALPHA move.", "%Timer", true);

set_global("ambush_triggered", true);
var pos := get_unit_position("ALPHA");
show_dialog("CONTACT! ALPHA is under fire! Request a contact report to learn about the enemy.\n\nSay: 'ALPHA, report contact'", true);
fail_objective("lane2_movement");

sleep(2.0);
var pos := get_unit_position("ALPHA");
show_dialog("Good! You now know VICTOR's position. Let me explain how combat works.", pos);
sleep(3.0);
show_dialog("COMBAT MECHANICS:

Units automatically fire when they have line-of-sight to enemies and are in range. You don't micromanage every shot.", true);
sleep(3.0);
show_dialog("HOW TO INFLUENCE COMBAT:

1. Call in SUPPORT: Order other units to attack the same target
2. FIRE SUPPORT: Use artillery/mortars (DELTA) to suppress enemies
3. FLANK: Attack from multiple directions
4. USE TERRAIN: Units in cover take less damage", true);
sleep(3.0);
show_dialog("For this tutorial, let's keep it simple. Directly engage VICTOR!

Say: 'ALPHA, attack VICTOR' or 'ALPHA, fire on VICTOR'", pos);

show_dialog("VICTOR destroyed! Now we need to break contact with BORIS.", true);
sleep(2.0);
show_dialog("Let's practice FIRE SUPPORT! You can suppress BORIS with mortars while ALPHA withdraws.\n\n(OPTIONAL) Say: 'DELTA, fire on BORIS' to suppress them.", true);
sleep(3.0);
show_dialog("Now order ALPHA to move to OBJ 2 (grid 631846).\n\nSay: 'ALPHA, move to OBJ 2'", true, vec2(2138, 648));

set_global("ambush_broke_contact", true);
complete_objective("lane2_combat");
sleep(2.0);
show_dialog("Excellent! ALPHA reached OBJ 2 safely. Lane 2 complete!\n\nNow for Lane 3: river crossing and smoke support.", true);
sleep(2.0);
show_dialog("First, we need a bridge. FOXTROT are engineers - they can build bridges.\n\nSay: 'FOXTROT, build bridge at grid 626843' (east of the river)", true, vec2(1687, 340));

set_global("bridge_built", true);
sleep(2.0);
show_dialog("Bridge complete! FOXTROT did their job. Now let's use smoke to cover ALPHA's crossing.", false, vec2(1687, 340));
sleep(2.0);
show_dialog("DELTA has mortars that can fire smoke. Call for fire support!\n\nSay: 'DELTA, fire smoke at grid 628845'", true);
complete_objective("lane3_bridge");

complete_objective("lane3_artillery")
sleep(2.0);
show_dialog("Smoke rounds away! Now send ALPHA across the bridge under the smoke cover.\n\nSay: 'ALPHA, move to OBJ 3' (grid 624843)", true, vec2(1449, 353));

complete_objective("lane3_retreat")
sleep(2.0);
show_dialog("Outstanding! ALPHA crossed successfully. Tutorial complete!

You've learned: radio commands, drawing, combat reports, engineer tasks, and fire support.

Check the Field Manual for advanced topics. Good luck, Commander!", true);
